void FT891::BuildUI(int w, int h)
{
	this->label("FT-891 Control");
	Tabs = new Fl_Tabs(0, 15, w, (h - 15),"");
	Tabs->callback((Fl_Callback*)&::OnTabChanged, this);
  	BuildGeneralGroup(w,h);
  	BuildPowerGroup(w,h);
	BuildLightingGroup(w,h);
	BuildRxEQGroup(w,h);
	BuildTXEQGroup(w,h);
	BuildCWSettings(w,h);
	BuildCatSettings(w, h);
	Tabs->show();
	Tabs->end();
	end();
}

